using System.Collections.Generic;
using System.IO;
using System.Linq;
using NUnit.Framework;
using NUnit.Framework.Legacy;
using Octodiff.Core;
using Octodiff.Diagnostics;
using Octodiff.Tests.Util;

namespace Octodiff.Tests.Core
{
    public class BinaryDeltaReaderFixture
    {
        static List<string> LogDeltaFile(byte[] input)
        {
            using (var ms = new MemoryStream(input))
            {
                var actions = new List<string>();

                var reader = new BinaryDeltaReader(ms, NullProgressReporter.Instance);
                reader.Apply(
                    writeData: (bytes, offset, length) =>
                    {
                        var slice = bytes.Skip(offset).Take(length).ToArray();
                        actions.Add($"write {slice.ToHexString()}");
                    }, copy: (start, length) => { actions.Add($"copy start={start}, length={length}"); });

                return actions;
            }
        }

        [Test]
        public void ReadsNoOpDeltaFile()
        {
            var input =
                "4f43544f44454c544101045348413114000000330bd06982d3b5dbda6c1a6ad16687a0cdb03c0d3e3e3e6000000000000000000802000000000000"
                    .HexStringToByteArray();

            CollectionAssert.AreEqual(new[]
            {
                "copy start=0, length=520"
            }, LogDeltaFile(input));
        }

        [Test]
        public void ReadsFullDeltaFile()
        {
            var input =
                ""
                    .HexStringToByteArray();

            CollectionAssert.AreEqual(new[]
            {
                "write "
            }, LogDeltaFile(input));
        }

        [Test]
        public void ReadsDeltaFileWithSmallChanges()
        {
            var input =
                ""
                    .HexStringToByteArray();

            CollectionAssert.AreEqual(new[]
            {
                "write 30820204308201aba003020102021418d83f07718be4121df0a18d7610faf8d7aaabac300a06082a8648ce3d0403023058310b30090603550406130241553113301106035504080c0a536f6d652d537461746531173015060355040a0c0e4f63746f707573204465706c6f79310c300a060355040b0c03522644310d300b0603",
                "copy start=128, length=392"
            }, LogDeltaFile(input));
        }

        [Test]
        public void ReadsDeltaFileWithSmallChanges_Prepend()
        {
            var input =
                "4f43544f44454c544101045348413114000000e5ca5051b8cf462ed567a8f88802fd9e62a0f0e83e3e3e800100000000000000aa6000000000000000000802000000000000"
                    .HexStringToByteArray();

            CollectionAssert.AreEqual(new[]
            {
                "write aa",
                "copy start=0, length=520"
            }, LogDeltaFile(input));
        }

        [Test]
        public void ReadsDeltaFileWithSmallChangesLargerFile()
        {
            var input =
                ""
                    .HexStringToByteArray();

            CollectionAssert.AreEqual(new[]
            {
                "write 30820204308201aba003020102021418d83f07718be4121df0a18d7610faf8d7aaabac300a06082a",
                "copy start=30720, length=100352",
                "write a3bec4300a06082a",
                "copy start=30720, length=28672",
                "write ",
            }, LogDeltaFile(input));
        }

        [Test]
        public void ReadsDeltaFileWithSmallChangesLargerFile_DisjointChanges()
        {
            var input =
                ""
                    .HexStringToByteArray();

            CollectionAssert.AreEqual(new[]
            {
                "write 30820204308201aba003020102021418d83f07718be4121df0a18d7610faf8d7aaabac300a06082a",
                "copy start=30720, length=30720",
                "write ",
                "copy start=53248, length=77824",
                "write a3bec4300a06082a",
                "copy start=30720, length=18432",
                "write ",
            }, LogDeltaFile(input));
        }
    }
}